const dropDown=document.getElementById("country-drop");dropDown.addEventListener("change",()=>{const value=dropDown.options[dropDown.selectedIndex].value,hideList=document.querySelectorAll(".country-hide"),showItem=document.querySelectorAll(`.${value}`);for(var i=0;i<hideList.length;i++)hideList[i].style.display="none";for(var i=0;i<showItem.length;i++)showItem[i].style.display="initial"});